import { useState , useEffect} from "react";
import axios from "axios";
import { useNavigate , useParams} from "react-router-dom";

export default function Edit(){
  
  const navigate = useNavigate();
  const [inputs, setinputs] = useState([]);
  const {_id} = useParams();

  // console.log({_id});
  
  useEffect(() => {
    getuser();
  } , []);
  
  function getuser(){
    axios.get(`http://localhost/React_CRUD/api/${_id}`).then(function(response){
      console.log(response.data);
      setinputs(response.data);
     });
   }

   const handleChange = (event) => {
     const name = event.target.name;
     const value = event.target.value;
     setinputs(values => ({...values, [name] : value}))
   } 

const handleSubmit =(event) => {
  event.preventDefault();
  axios.post('http://localhost/React_CRUD/api', inputs).then(function(response){
    console.log(response.data);
    navigate('/');
  });
}

  return(
    <div>
      <h4>Edit User</h4>
      <form onSubmit={handleSubmit}>
        <lable>Name</lable>
        <input type="text" value={inputs.name} name='name' onChange={handleChange} />
        <br/>
        <lable>Email</lable>
        <input  type="email" value={inputs.email} name='email' onChange={handleChange} />
        <br/>
        <lable>Password</lable>
        <input  type="text" value={inputs.password} name='password' onChange={handleChange}/>
        <br/>
        <button>Save</button>
      </form>
    </div>
  )
}
