import axios from "axios"
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";

export default function List(){

  const [users , setUsers] = useState([ ]);

  useEffect(() => {
    getusers();
  } , []);

   function getusers(){
     axios.get('http://localhost/React_CRUD/api/').then(function(response){
       console.log(response.data);
       setUsers(response.data);
      });
    }

  return(
    <div>
        <h1>List</h1>
        <table>
          <thead>
            <tr>
                <th>#</th>
                <th>Name</th>
                <th>Email</th>
                <th>Password</th>
                <th>Action</th>
            </tr>
          </thead>
          <tbody>
                  {users.map((user, key) => 
                    <tr key={key}>
                        <td>{user._id["$oid"]}</td>
                        {/* <td>{key +1 }</td> */}
                        <td>{user.name}</td>
                        <td>{user.email}</td>
                        <td>{user.password}</td>
                        <td>
                          <Link to={`api/${user._id["$oid"]}/edit`} style={{ marginRight:"10px" }}>Edit</Link>
                          {/* <Link to={`user/${key}/edit`} style={{ marginRight:"10px" }}>Edit</Link> */}
                          <button>Delete</button>
                        </td>
                    </tr>
                  )}
          </tbody>
        </table>
    </div>
  )
}
